mapper.select:
	
	select *
	from ( 
    select A.FWLBNO, A.FWLBWRITER, A.FWLBTITLE, A.FWLBREADCNT, A.FWLBDATE,
    ROWNUM AS RN
    from (
        select *
        from FWLB ORDER BY FWLBNO DESC) A
    where ROWNUM <= #{endPost}) X
	where X.RN >= #{startPost}
	ORDER BY X.FWLBNO DESC

	
	select X.RN
	from ( 
    select A.FWLBNO, A.FWLBWRITER, A.FWLBTITLE, A.FWLBREADCNT, A.FWLBDATE,
    ROWNUM AS RN
    from (
        select *
        from FWLB ORDER BY FWLBNO DESC) A
    where ROWNUM <= #{endPost}) X
	where X.RN >= #{startPost}
	ORDER BY X.FWLBNO
